resultado <- vector()
y = 1

repeat{
  if(y > 1){
    x <- sum(c(resultado[1:(y-1)], iris$Sepal.Length[y] + iris$Petal.Length[y]))  
  } else {
   x <- iris$Sepal.Length[y] + iris$Petal.Length[y]  
  }

  if(x > 100){
    break
  } else {
    resultado[[y]] <- x
    y <- y + 1
  }
}

map_chr(entrada, function(x){
  x.1 <- unique(as.character(unique(x$Species)))
  return(x.1)
})
#>       setosa   versicolor    virginica 
#>     "setosa" "versicolor"  "virginica"

map_lgl(entrada, function(x){
  x.1 <- unique(as.character(unique(x$Species))) == "setosa"
  return(x.1)
})
#>     setosa versicolor  virginica 
#>       TRUE      FALSE      FALSE

